def sort_books_publisher(book_lst: dict) -> list:

    """Returns a list of books given the dictionary, organized alphabetically by publisher.
    
    >>>>>>sort_books_author(funny_dict.csv)
    {'title': 'Shantaram', 'author': 'Gregory David Roberts', 'rating': 4.5, 'publisher': 'Hachette UK', 'pages': '944', 'language\n': 'English', 'category': ['Fiction', 'Detective', 'Thrillers']}, {'title': 'Sword of Destiny: Witcher 2: Tales of the Witcher', 'author': 'Andrzej Sapkowski', 'rating': 4.8, 'publisher': 'Hachette UK', 'pages': '400', 'language\n': 'English', 'category': ['Fiction', 'Adventure', 'Mythical Creatures']}, {'title': 'Tall Tales and Wee Stories: The Best of Billy Connolly', 'author': 'Billy Connolly', 'rating': 5.0, 'publisher': 'Hachette UK', 'pages': '336', 'language\n': 'English', 'category': ['Humor', 'Biography']}
    """
  
    for i in range(len(book_lst)):
        for j in range(len(book_lst)-i-1) :
            if book_lst[j]['publisher'] > book_lst[j+1]['publisher']:
                book_lst[j] ,book_lst[j+1] =book_lst[j+1],book_lst[j]
            elif  book_lst[j]['publisher'] == book_lst[j+1]['publisher'] and book_lst[j]['title'] > book_lst[j+1]['title']:
                book_lst[j] ,book_lst[j+1] =book_lst[j+1],book_lst[j]
   

    return book_lst

#Function 3 - Matt Turner (101169414)
def sort_books_author(book_lst: dict) -> list:
   
    """
    This function takes the milestone 1- case 1 - lab 1 dictionary, and creates a list
    where the items are all the books with their data stored as items in dictionaries.
    It then sorts through the list of dictionaries using bubble sort to put the
    book authors in alphabetical order.
    
    >>>sort_books_author(random_dict.csv)
    {'title': 'Bring Me Back', 'author': 'B A Paris', 'rating': 3.8, 'publisher': 'HarperCollins UK', 'pages': '368', 'language\n': 'English', 'category': ['Fiction', 'Crime', 'Thrillers']}, {'title': 'Antiques Chop', 'author': 'Barbara Allan', 'rating': 4.5, 'publisher': 'Kensington Books', 'pages': '240', 'language\n': 'English', 'category': ['Fiction', 'Detective', 'Mystery']}, {'title': 'Antiques Con', 'author': 'Barbara Allan', 'rating': 4.8, 'publisher': 'Kensington Books', 'pages': '288', 'language\n': 'English', 'category': ['Fiction', 'Detective', 'Mystery']}
    """
      
    for i in range(len(book_lst)):
        for j in range(len(book_lst)-i-1) :
            if book_lst[j]['author'] > book_lst[j+1]['author']:
                book_lst[j] ,book_lst[j+1] =book_lst[j+1],book_lst[j]
            elif  book_lst[j]['author'] == book_lst[j+1]['author'] and book_lst[j]['title'] > book_lst[j+1]['title']:
                book_lst[j] ,book_lst[j+1] =book_lst[j+1],book_lst[j]
   
   
               
    return book_lst

#Function 4 - Pietro Adamvoski (101238885)
def sort_books_ascending_rate(book_lst:dict)->list:
    """Returns a list with the book data stored as a dictionary book where 
    the books are sorted by their their rate in ascending order
    
    >>>sort_books_ascending_rate(random_dict.csv)
    [{'title': 'The Guardians: The explosive new thriller from international
    bestseller John Grisham', 'author': 'John Grisham', 'rating': 'N/A', 
    'publisher': 'Hachette UK', 'pages': '384', 'language\n': 'English', 
    'category': ['Fiction', 'Thrillers', 'Legal']}, {'title': 'No Mercy: 
    The brand new novel from the Queen of Crime', 'author': 'Martina Cole', 
    'rating': 'N/A', 'publisher': 'Hachette UK', 'pages': '416', 'language\n': 
    'English', 'category': ['Fiction', 'Thrillers']}, {'title': 'Marketing 
    (The Brian Tracy Success Library)', 'author': 'Brian Tracy', 'rating': 
    'N/A','publisher': 'AMACOM', 'pages': '112', 'language\n': 'English', 
    'category': ['Economics']} 

    """
    
                
  
    number_of_books=len(book_lst)
  
    for i in range(number_of_books):
        for j in range(number_of_books-i-1):
            if book_lst[j]['rating']!='N/A':
                if book_lst[j+1]['rating']=='N/A':
                    book_lst[j],book_lst[j+1]=book_lst[j+1],book_lst[j]
                elif book_lst[j]['rating']==book_lst[j+1]['rating']:
                    if book_lst[j]['title']>book_lst[j+1]['title']:
                        book_lst[j],book_lst[j+1]=book_lst[j+1],book_lst[j]                
                elif book_lst[j]['rating']>book_lst[j+1]['rating']:
                    book_lst[j],book_lst[j+1]=book_lst[j+1],book_lst[j]
                
  
    return book_lst


